function resizeButton(){let e=document.querySelectorAll(".room-info__button"),o=[].map.call(e,e=>e.offsetHeight),t=Math.max(...o);e.forEach(e=>{e.style.minHeight=t+"px"})}function reserveRoom(){let e=this.event.target;e.closest(".room")&&changeStyle(e.closest(".room"))}function changeStyle(e){e.classList&&(e.classList.add("room_active"),e.addEventListener("mouseleave",()=>{e.classList.remove("room_active"),e.classList.add("room_reserved")},{once:!0}))}function cancelReserve(){document.addEventListener("click",({target:e})=>{"room__reserved-link"!==e.className&&e.closest(".room.room_reserved")&&e.closest(".room.room_reserved").classList.remove("room_reserved")})}function resizePrice(){let e=document.querySelectorAll(".room-info__price");console.log(e);let o=[].map.call(e,e=>e.offsetHeight),t=Math.max(...o);e.forEach(e=>{e.style.minHeight=t+"px"})}resizeButton(),cancelReserve(),resizePrice();