function reserveRoom(){document.addEventListener("click",({target:e})=>{"room-info__button"===e.className&&e.closest(".room")&&changeStyle(e.closest(".room"))})}function changeStyle(e){e.classList&&(e.classList.add("room_active"),e.addEventListener("mouseleave",()=>{e.classList.remove("room_active"),e.classList.add("room_reserved")},{once:!0}))}function cancelReserve(){document.addEventListener("click",({target:e})=>{"room__reserved-link"!==e.className&&e.closest(".room.room_reserved")&&e.closest(".room.room_reserved").classList.remove("room_reserved")})}reserveRoom(),cancelReserve();